import os
import flume
import flume.flmos as flmo

def uname2etag (uname, umgruid): 
    import flmw.util
    p = flmw.util.username2User (uname, umgruid)
    p.readTags ()
    return p._e_tag

def uname2wtag (uname, umgruid): 
    import flmw.util
    p = flmw.util.username2User (uname, umgruid)
    p.readTags ()
    return p._w_tag

def uname2label (uname, umgruid):
    # Currently only suports etag
    return flmo.Label([uname2etag(uname, umgruid)])

